<?phpclass AlbumController extends Controller{   /**    * Если действие определено в форме класса,    * то нужно информировать контроллер об этом действии    * с помощью метода actions    */    public function actions(){                $var1 = '111';        $var2 = '222';                return array(            'add' => array(                'class' => 'application.controllers.album.AddAction',               'property1' => $var1,                'property2' => $var2            )        );            }        /*    * Назначаем фильтры на определенные действия.    * Если фильтры не назначены, они применяться не будут.    * Фильтры могут быть определены как в форме метода, так и в форме класса.    */    public function filters(){                return array(            // назначаем фильтр filterAcsessControl для действия index            'acsessControl + index'        );            }        /*    * --------------------------------------    * Действия, определенные в форме метода    * --------------------------------------    */        public function actionIndex()    {        //пример обращения к компонентам приложения        //Yii::app()->log;                echo "Album index"; exit;            }            /*     * ----------------------------------------    * Фильтры, определенные в форме метода    * ----------------------------------------    */        /*    * Фильтр контроля доступа    */    public function filterAcsessControl($filterChain){                if(1 != 1){            // прерываем выполнение            return false;        }        else{            // продолжаем выполнение остальных фильтров            $filterChain->run();        }            }}